mapper.select:
SELECT m.menu_id, m.menu_name, m.menu_url, m.menu_class, m.parent_id, m.level, m.status FROM t_sms_menu m, t_sms_role_menu rm, t_sms_role r WHERE m.menu_id = rm.menu_id AND rm.role_id = r.id AND m.menu_type = 1 AND m.`status` = 1 AND r.`status` = 1
AND m.`level` >= #{level}
AND r.id = #{role_id} ORDER BY m.level, m.parent_id, m.sort
